import axios from "axios";
import React, { useState } from "react";
import "../style/Search.scss";
import imagen from "../assets/hombres_trabajando-400x315.png";

const Search = () => {
  let key = "e9ffcce562635a23124ca8069487f842";
  const [search, setSearch] = useState([]); //Buscar
  const [value, setValue] = useState("");

  const handlerClick = (e) => {
    setValue(e.target.value); //Buscar el titulo de una pelí en puntual
  };

  const handlerFav=(peli,i)=>{
    axios.put('http://localhost:1337/api/favorites',{
      title:peli.title,
      path:peli.backdrop_path,
      movieId:i,

    })
  }

  const handlerSubmit = (e) => {
    e.preventDefault();
    axios
      .get(
        `https://api.themoviedb.org/3/search/movie?api_key=${key}&query=${value}`
      )
      .then((res) => res.data)
      .then((search) => setSearch(search.results));
  };
  return (
    <div>
      <form onSubmit={handlerSubmit}>
        <label>
          <input
            type="text"
            placeholder="Buscate una para el mate"
            onChange={handlerClick}
          ></input>
        </label>
        <button type="submit">search</button>
        {search.map((peli, i) => {
          return (
            <div class="card">
              <img
                className="img"
                src={
                  !peli.backdrop_path
                    ? imagen
                    : `https://image.tmdb.org/t/p/w500${peli.backdrop_path}`
                }
              ></img>
              <button onClick={()=>{handlerFav(peli,i)}}></button>
              <div class="content">
                <p>
                  <strong>{peli.title}</strong>
                </p>
              </div>
            </div>
          );
        })}
      </form>
    </div>
  );
};

export default Search;
