import axios from "axios";
import React, { useState } from "react";

const App = () => {
  const [search, setSearch] = useState({}); //Buscar el titulo de una pelí en puntual

  const handlerClick = (e) => {
    setSearch(e.target.value); // funciona si escribo
  };

  const handlerSubmit = (e) => {
    e.preventDefault();
    axios
      .get(
        "https://api.themoviedb.org/3/search/movie?api_key=e9ffcce562635a23124ca8069487f842c&query=Jack+Reacher"
      )
      .then((res) => res.data)
      .then((search) => setSearch(search.results[0].title));
  };

  return (
    <form onSubmit={handlerSubmit}>
      <label>
        <input type="text" key={search.id}>
          {search.title}
        </input>
      </label>
      <button type="submit">search</button>
      <p>{search}</p>
    </form>
  );
};

export default App;
