import React, { useContext } from "react";
import useInput from "../hooks/Useimput";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { AuthContext } from "../contex/Authenticate";

const Login = () => {
  const state = useContext(AuthContext);
  const email = useInput();
  const password = useInput();
  const navegate = useNavigate();

  const handlerSumit = (e) => {
    e.preventDefault();
    axios
      .post("http://localhost:1337/api/users/login", {
        email: email.value,
        password: password.value,
      })
      .then((res) => {state.toggleAuth(res.data.name)
        localStorage.setItem("user",JSON.stringify(res.data))
      })
      .then(() => navegate("/"))
      .catch(() =>
        alert(
          "Mensajes de error: Contraseña y/o nombre de cuenta incorrecto \n MAMERTE "
        )
      );
  };
  return (
    <div>
      <form onSubmit={handlerSumit}>
        <div class="mb-3">
          <label for="exampleInputEmail1" class="form-label">
            Email address
          </label>
          <input
            {...email}
            type="email"
            class="form-control"
            id="exampleInputEmail1"
            aria-describedby="emailHelp"
            placeholder="Well never share your email with anyone else"
          />
        </div>
        <div class="mb-3">
          <label for="exampleInputPassword1" class="form-label">
            Password
          </label>
          <input
            {...password}
            type="password"
            class="form-control"
            id="exampleInputPassword1"
          />
        </div>

        <button type="submit" class="btn btn-primary">
          Submit
        </button>
      </form>
    </div>
  );
};

export default Login;
