import axios from "axios";
import React, { useEffect, useState } from "react";


const Search = () => {
  let add = "Add fav";
  let key = "e9ffcce562635a23124ca8069487f842";
  const [search, setSearch] = useState([]); //Buscar
  const [value, setValue] = useState("");
  const [fav, setFav] = useState([]);

  useEffect(() => {
    const userStorage = JSON.parse(localStorage.getItem("name")).id;
    axios
      .get(`http://localhost:1337/api/favorites/${userStorage}`)
      .then((res) => setFav(res.data));

  }, []);
  const code = fav.map((fav) => fav.movieId);

  const handlerClick = (e) => {
    setValue(e.target.value); //Buscar el titulo de una pelí en puntual
  };

  const handlerFav = (peli) => {
    const userStorage = JSON.parse(localStorage.getItem("name")).id;
    axios.put("http://localhost:1337/api/favorites", {
      title: peli.title,
      path: peli.backdrop_path,
      movieId: peli.id,
      overview: peli.overview,
      userId: userStorage,
    });
  };

  const handlerDelete = (peli, i) => {
    const userStorage = JSON.parse(localStorage.getItem("name")).id;
    axios.delete("http://localhost:1337/api/favorites/delete", {
      data: {
        title: peli.title,
        path: peli.backdrop_path,
        movieId: peli.id,
        overview: peli.overview,
        userId: userStorage,
      },
    });
  };

  const handlerSubmit = (e) => {
    e.preventDefault();
    axios
      .get(
        `https://api.themoviedb.org/3/search/movie?api_key=${key}&language=es&query=${value}`
      )
      .then((res) => res.data)
      .then((search) => setSearch(search.results));
  };
  return (
    <div>
      <form onSubmit={handlerSubmit}>
        <label>
          <input
            type="text"
            placeholder="Buscate una para el mate"
            onChange={handlerClick}
          ></input>
        </label>
        <button type="submit">search</button>
        {search.map((peli, i) => {
          return (
            <>
              <div class="card">
                <img
                  alt="..."
                  className="img"
                  src={
                    !peli.backdrop_path
                      ? `https://image.tmdb.org/t/p/w780${peli.poster_path}`
                      : `https://image.tmdb.org/t/p/w780${peli.backdrop_path}`
                  }
                ></img>
                <div class="content">
                  <p>
                    <strong>{peli.title}</strong>
                  </p>
                </div>
              </div>
              <p>{peli.overview}</p>
              {!code.includes(peli.id) && (
                <button
                  onClick={() => {
                    handlerFav(peli);
                  }}
                >
                  {add}
                </button>
              )}
              {code.includes(peli.id) && (
                <button
                  onClick={() => {
                    handlerDelete(peli, i);
                  }}
                >
                  Delete
                </button>
              )}
            </>
          );
        })}
      </form>
    </div>
  );
};

export default Search;
