import axios from "axios";
import React, { useState } from "react";
import "../style/Search.scss";
import imagen from '../assets/hombres_trabajando-400x315.png'

const Search = () => {
  let key = "e9ffcce562635a23124ca8069487f842";
  const [search, setSearch] = useState([]); //Buscar
  const [value, setValue] = useState("");

  const handlerClick = (e) => {
    setValue(e.target.value); //Buscar el titulo de una pelí en puntual
  };

  const handlerSubmit = (e) => {
    e.preventDefault();
    axios
      .get(
        `https://api.themoviedb.org/3/search/movie?api_key=${key}&query=${value}`
      )
      .then((res) => res.data)
      .then((search) => setSearch(search.results));
  };
  return (
    <div>
      <form onSubmit={handlerSubmit}>
        <label>
          <input
            type="text"
            placeholder="Buscate una para el mate"
            onChange={handlerClick}
          ></input>
        </label>
        <button type="submit">search</button>
        {search.map((peli, i) => {
          return (
            // <ul className="moviCard">
            //   <li><img className="moviImage" src={`https://image.tmdb.org/t/p/w780${peli.backdrop_path}`}
            //     alt="imagen"></img></li>
            //   <li key={i}>{peli.title}</li>
            //   <li>{peli.overview}</li>
            // </ul>
            <div class="card">
                {(peli.backdrop_path==null)
                  ? {imagen}
                :<img className="img" src={`https://image.tmdb.org/t/p/w500${peli.backdrop_path}`}
                ></img>}
              <div class="content">
                <p><strong>{peli.title}</strong></p>
              </div>
            </div>
          );
        })}
      </form>
    </div>
  );
};

export default Search;
