import axios from "axios";
import React, { useState } from "react";

const App = () => {
  const [search, setSearch] = useState([]); //Buscar el titulo de una pelí en puntual

  const handlerClick = (e) => {
    setSearch(e.target.value); // funciona si escribo
  };

  const handlerSubmit = (e) => {
    e.preventDefault();
    axios
      .get(
        "https://api.themoviedb.org/3/search/movie?api_key=e9ffcce562635a23124ca8069487f842&query=Jack+Reacher"
      )
      .then((res) => res.data)
      .then((search) => setSearch(search.results));
  };

  return (
    <form onSubmit={handlerSubmit}>
      <label>
        <input type="text" placeholder="Buscate una para el mate" onChange={handlerClick}></input>
      </label>
      <button type="submit">search</button>
     {search.map((peli,i)=>{
       return(
         <p key={i}>{peli.title}</p>
       )
     })}
    </form>
  );
};

export default App;
