import axios from "axios";
import React, { useState } from "react";

const UserSearch = () => {
  const [user, setUser] = useState("");
  const [perfil,setPerfil]=useState([])

  const handlerSearch = (e) => {
    setUser(e.target.value); //Buscar el titulo de una pelí en puntual
  };

  const handlerSubmit = (e) => {
    e.preventDefault();
    axios
      .get(`http://localhost:1337/api/users/${user}`)
      .then((user) => setPerfil(user.data))
      .catch((err) => console.log(err));
  };
  return (
    <div>
      <form onSubmit={handlerSubmit}>
        <label>
          <input
            type="text"
            placeholder="Stalkeate una"
            onChange={handlerSearch}
          ></input>
        </label>
        <button type="submit">search</button>
        {perfil.map((peli, i) => {
          return (
            <>
              <div class="card">
                <img
                  alt="..."
                  className="img"
                  src={
                    !peli.backdrop_path
                      ? `https://image.tmdb.org/t/p/w780${peli.path}`
                      : `https://image.tmdb.org/t/p/w780${peli.backdrop_path}`
                  }
                ></img>
                <div class="content">
                  <p>
                    <strong>{peli.title}</strong>
                  </p>
                </div>
              </div>

    </>
    )})}
    </form>
    </div>)
}

export default UserSearch;
