import axios from "axios";
import React, { useEffect, useState } from "react";
import "../style/Search.scss";

const Search = () => {
  let add = "Add fav";
  let key = "e9ffcce562635a23124ca8069487f842";
  const [search, setSearch] = useState([]); //Buscar
  const [value, setValue] = useState("");

  const handlerClick = (e) => {
    setValue(e.target.value); //Buscar el titulo de una pelÃ­ en puntual
  };

  const handlerFav = (peli) => {
    const userStorage = JSON.parse(localStorage.getItem("name")).id;
    axios.put("http://localhost:1337/api/favorites", {
      title: peli.title,
      path: peli.backdrop_path,
      movieId: peli.id,
      overview: peli.overview,
      userId: userStorage,
    });
  };

  const handlerDelete = (peli, i) => {
    const userStorage = JSON.parse(localStorage.getItem("name")).id;
    axios.delete("http://localhost:1337/api/favorites/delete", {
      data: {
        title: peli.title,
        path: peli.backdrop_path,
        movieId: peli.id,
        overview: peli.overview,
        userId: userStorage,
      },
    });
  };

  const handlerSubmit = (e) => {
    e.preventDefault();
    axios
      .get(
        `https://api.themoviedb.org/3/search/movie?api_key=${key}&language=es&query=${value}`
      )
      .then((res) => res.data)
      .then((search) => setSearch(search.results));
  };
  return (
    <div>
      <form onSubmit={handlerSubmit} className='buscar'>
        <input
          type="text"
          placeholder="Buscate una para el mate"
          onChange={handlerClick}
        ></input>
        <button type="submit">search</button>
      </form>
      <div className="content">
        {search.map((peli, i) => {
          return (

            <div>
              <div className="card">
                <img
                  alt="..."
                  src={
                    !peli.backdrop_path
                      ? `https://image.tmdb.org/t/p/w780${peli.poster_path}`
                      : `https://image.tmdb.org/t/p/w780${peli.backdrop_path}`
                  }
                ></img>
                <div className="card__inner">
                  <h4>
                    <strong>{peli.title}</strong>
                  </h4>
                  <p>{peli.overview}</p>
                </div>
              </div>
              <div className="contentBtn">
                <button className="btn"
                  onClick={() => {
                    handlerFav(peli);
                  }}
                >
                  {add}
                </button>

                <button className="btn"
                  onClick={() => {
                    handlerDelete(peli, i);
                  }}
                >
                  Delete
                </button>
              </div>
              </div>

          );
        })}
      </div>
    </div>
  );
};

export default Search;

<div class="wrap">
   <div class="search">
      <input onChange={handlerClick} type="text" class="searchTerm" placeholder="What are you looking for?"/>
      <button type="submit" class="searchButton">
        <i class="fa fa-search"></i>
     </button>
   </div>
</div>
