import axios from "axios";
import React, { useEffect,useState } from "react";

const Perfiluser = () => {
  const [favoritos, setFavoritos] = useState([]);
  const [favShow, setFavShow] = useState([]);
  const favArray = [];
  const userStorage = JSON.parse(localStorage.getItem("name")).id;



  useEffect(() => {
    axios
      .get(`http://localhost:1337/api/favorites/${userStorage}`)
      .then((data) => setFavoritos(data));
      console.log(favoritos);

  });


  const handleFavorit = (favorito) => {
    axios
      .get(
        `https://api.themoviedb.org/3/movie/${favoritos.code}?api_key=7cbf5b5c696ab8395fd5dd2f6b402a50`
      )
      .then((fav) => favArray.push(fav.data));
  };



  return (
    <>
      <div>
        {/* <>{(favoritos)
    ?favoritos.map(favorito => handleFavorit(favorito))
    :"You don't have favorites films"}
    </> */}
        <div>
          {favArray.map((fav, i) => {
            return (
              <>
                <img
                  src={`https://image.tmdb.org/t/p/w342${fav.poster_path}`}
                  alt="imagen"
                />
                <p> {fav.title}</p>
                <p>{fav.overview}</p>
              </>
            );
          })}
        </div>
      </div>
    </>
  );
};

export default Perfiluser;
