import axios from "axios";
import React, { useState } from "react";
import '../style/Search.scss'

const Search = () => {
  let key = "e9ffcce562635a23124ca8069487f842";
  const [search, setSearch] = useState([]); //Buscar
  const [value, setValue] = useState("");

  const handlerClick = (e) => {
    setValue(e.target.value); //Buscar el titulo de una pelí en puntual
  };

  const handlerSubmit = (e) => {
    e.preventDefault();
    axios
      .get(
        `https://api.themoviedb.org/3/search/movie?api_key=${key}&query=${value}`
      )
      .then((res) => res.data)
      .then((search) => setSearch(search.results));
  };
  return (
    <div>
      <form onSubmit={handlerSubmit}>
        <label>
          <input
            type="text"
            placeholder="Buscate una para el mate"
            onChange={handlerClick}
          ></input>
        </label>
        <button type="submit">search</button>
        {search.map((peli, i) => {
          return (
            <div className="moviCard">
              <p key={i}>{peli.title}</p>
              <p>{peli.overview}</p>
              <img className="movieImage"
                src={`https://image.tmdb.org/t/p/w500${peli.backdrop_path}`}
                alt="imagen"
              ></img>
            </div>
          );
        })}
      </form>
    </div>
  );
};

export default Search;
