import axios from "axios";
import React, { useEffect, useState } from "react";

const Slider = () => {
  let key = "e9ffcce562635a23124ca8069487f842";
  const [movie, setMovie] = useState([]);

  useEffect(() => {
    axios
      .get(`https://api.themoviedb.org/3/discover/movie?api_key=${key}`)
      .then((res) => res.data)
      .then((data) => setMovie(data.results));
  }, []);

  return (
    <div className="slider">
      <ul>
        {movie.map((peli, i) => {
          return (
            <>
              <li key={i}>
                <img
                  src={`https://image.tmdb.org/t/p/w1280${peli.backdrop_path}`}
                  alt="imagen"
                ></img>
                <p>{peli.title}</p>
              </li>
            </>
          );
        })}
      </ul>
    </div>
  );
};

export default Slider;

<div id="carouselExampleCaptions" class="carousel slide" data-bs-ride="false">
  <div class="carousel-indicators">
    <button
      type="button"
      data-bs-target="#carouselExampleCaptions"
      data-bs-slide-to="0"
      class="active"
      aria-current="true"
      aria-label="Slide 1"
    ></button>
    <button
      type="button"
      data-bs-target="#carouselExampleCaptions"
      data-bs-slide-to="1"
      aria-label="Slide 2"
    ></button>
    <button
      type="button"
      data-bs-target="#carouselExampleCaptions"
      data-bs-slide-to="2"
      aria-label="Slide 3"
    ></button>
  </div>
  {movie.map((peli,i)=>{
    return(
      <div class="carousel-inner">
    <div class="carousel-item active">
      <img src={
                    !peli.backdrop_path
                      ? `https://image.tmdb.org/t/p/w780${peli.poster_path}`
                      : `https://image.tmdb.org/t/p/w780${peli.backdrop_path}`
                  } class="d-block w-100" alt="..." />
      <div class="carousel-caption d-none d-md-block">
        <h5>First slide label</h5>
        <p>Some representative placeholder content for the first slide.</p>
      </div>
    </div>
  </div>
    )
  })}

  <button
    class="carousel-control-prev"
    type="button"
    data-bs-target="#carouselExampleCaptions"
    data-bs-slide="prev"
  >
    <span class="carousel-control-prev-icon" aria-hidden="true"></span>
    <span class="visually-hidden">Previous</span>
  </button>
  <button
    class="carousel-control-next"
    type="button"
    data-bs-target="#carouselExampleCaptions"
    data-bs-slide="next"
  >
    <span class="carousel-control-next-icon" aria-hidden="true"></span>
    <span class="visually-hidden">Next</span>
  </button>
</div>;
