import axios from "axios";
import React, { useEffect, useState } from "react";

const Perfiluser = () => {
  let key = "e9ffcce562635a23124ca8069487f842";
  const [favoritos, setFavoritos] = useState([]);
  const [favShow, setFavShow] = useState([]);
  const userStorage = JSON.parse(localStorage.getItem("name")).id;

  useEffect(() => {
    axios
      .get(`http://localhost:1337/api/favorites/${userStorage}`)
      .then((res) => setFavoritos(res.data));
  }, []);

  useEffect(() => {
    console.log(favoritos)
    axios
      .get(
        `https://api.themoviedb.org/3/movie/${414906}?api_key=7cbf5b5c696ab8395fd5dd2f6b402a50`
      )
      .then((fav) => setFavShow(fav.data));
  });

  return (
    <>
    <div>

        <div>
          {favShow.map((fav, i) => {
            return (
              <>
                <img src={fav.poster_path}></img>
                <p>{fav.title}</p>

              </>
            );
          })}
        </div>
      </div>
    </>
  );
};

export default Perfiluser;
